import { useState } from "react";

const DiaryEditor = () => {
  const [state, setState] = useState({
    author: "",
    content: "",
  });
  const handelChangeState = (e) => {
    setState({
      ...state,
      [e.target.name]: e.target.value,
    });
  };

  return (
    <div className="DiaryEditor">
      <h2>오늘의 일기!</h2>
      <div>
        <input
          name="author"
          value={state.author}
          onChange={handelChangeState}
        />
      </div>
      <div>
        <textarea
          name="content"
          value={state.content}
          onChange={handelChangeState}
        />
      </div>
    </div>
  );
};

export default DiaryEditor;
