function App() {
  const [data, setData] = useState([]);

  const dataId = useRef();

  const onCreate = (author, content, emotion) => {
    const created_date = new Date().getTime();

    const newItem = {
      author,
      content,
      emotion,
      created_date,
      id: dataId.current,
    };

    dataId.current += 1;
    setData([newItem, ...data]);
  };

  return (
    <div className="App">
      <DiaryEditor />
      <DiaryList diaryList={onCreate} />
    </div>
  );
}

export default App;
